import {useParams} from "react-router-dom";
import {useEffect, useRef, useState} from "react";
import {FileUpload} from "primereact/fileupload";
import {loadChallenge} from "../../../integrations/challenge-loader";
import {Galleria} from "primereact/galleria";

export function Detail() {
    const {id, challenge} = useParams();
    const [challengeDetail, setChallengeDetails] = useState({})
    const [activeIndex, setActiveIndex] = useState(0);
    const galleria = useRef(null);
    useEffect(() => {
        const start = async () => {
            const c = await loadChallenge(id, challenge)
            setChallengeDetails(c)
        }
        start()
    }, [id, challenge])

    const itemTemplate = (item) => {
        return <img src={item.itemImageSrc} alt={item.alt} style={{ width: '100%', display: 'block' }} />;
    }

    const thumbnailTemplate = (item) => {
        return <img src={item.thumbnailImageSrc} alt={item.alt} style={{ display: 'block' }} />;
    }

    return (
        <div className={"flex flex-column align-items-center justify-content-center"}>
            <div className={"flex flex-column justify-content-center align-items-center p-2"}>
                <h2>{challengeDetail.title}</h2>
                <p>{challengeDetail.description}</p>
            </div>
            <div className={"flex flex-column p-5"}>
                <FileUpload name="demo[]" url={'/api/upload'}
                            multiple
                            accept="image/*"
                            />
            </div>
            <div className={"flex flex-wrap flex-grow-1 justify-content-center align-items-center"}>
                <Galleria ref={galleria} value={images} numVisible={7} style={{ maxWidth: '850px' }}
                          activeIndex={activeIndex} onItemChange={(e) => setActiveIndex(e.index)}
                          circular fullScreen showItemNavigators showThumbnails={false} item={itemTemplate} thumbnail={thumbnailTemplate} />
            </div>
            <div className="flex flex-wrap flex-grow-1 justify-content-center align-items-center" style={{ maxWidth: '400px' }}>
                {
                    challengeDetail.images && challengeDetail.images.map((image, index) => {
                        let imgEl = <img alt={image.alt_text} src={image.url} onClick={
                            () => {setActiveIndex(index); galleria.current.show()}
                        } />
                        return (
                            <div className="col-3" key={index}>
                                {imgEl}
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}
