import {useParams} from "react-router-dom";
import {useEffect} from "react";
import {loadImages} from "../../../integrations/image-loader";

export function Detail() {
    const {id, challenge} = useParams();
    const [images, setImages] = useState({})

    useEffect(() => {
        const start = async () => {
            const c = await loadImages(id, challenge)
            setImages(c)
        }
        start()
    })

    return (
        <>
            <div className={"flex flex-column"}>
                Detail - {challenge}
            </div>
            <div className={"flex flex-column"}>
                {
                    images && images.images && images.images.map((image, index) => (
                        <div key={index} className={"flex justify-content-center"}>
                            <img src={image.url} alt={image.description} width="250" />
                        </div>
                    ))
                }}
            </div>
        </>
    )
}
