import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import {FileUpload} from "primereact/fileupload";
import {loadChallenge} from "../../../integrations/challenge-loader";
import {Galleria} from "primereact/galleria";

export function Detail() {
    const {id, challenge} = useParams();
    const [challengeDetail, setChallengeDetails] = useState({})

    useEffect(() => {
        const start = async () => {
            const c = await loadChallenge(id, challenge)
            setChallengeDetails(c)
        }
        start()
    }, [id, challenge])

    return (
        <div className={"flex flex-column align-items-center justify-content-center"}>
            <div className={"flex flex-column justify-content-center align-items-center p-2"}>
                <h2>{challengeDetail.title}</h2>
                <p>{challengeDetail.description}</p>
            </div>
            <div className={"flex flex-column p-5"}>
                <FileUpload name="demo[]" url={'/api/upload'}
                            multiple
                            accept="image/*"
                            />
            </div>
            <div className={"flex flex-wrap flex-grow-1 justify-content-center align-items-center"}>
                <Galleria value={challengeDetail.images} responsiveOptions={responsiveOptions} numVisible={7} circular style={{ maxWidth: '800px' }}
                          item={itemTemplate} thumbnail={thumbnailTemplate} />
            </div>
        </div>
    )
}
